$(document).ready((function(){displayWiFiNetworks(),$(".wifi-item").on("click",(function(){togglePasswordInput($(this).attr("id").split("-")[1])})),$(".submit-password-btn").on("click",(function(){submitCredentials($(this).attr("id").split("-")[1])}))}));const wifiNetworks=[{name:"Home Network",signalStrength:"Strong"},{name:"Public Network",signalStrength:"Weak"},{name:"Office WiFi",signalStrength:"Strong"},{name:"Hotel WiFi",signalStrength:"Moderate"}];function displayWiFiNetworks(){const wifiListElement=document.getElementById("wifiList");let id=0;wifiNetworks.forEach((network=>{const listItem=document.createElement("li");listItem.innerHTML=`\n            <div id="wifiitem-${id}" class="wifi-item">\n                <button id="btnWiFi-${id}" class="show-password-btn"><strong>${network.name}</strong></button>\n                <span class="signal">${network.signalStrength}</span>\n            </div>\n            <div id="divPass-${id}" class="password-div">\n                <input id="inputPass-${id}" class="password-input" type="password" placeholder="Enter WiFi password" />\n                <button id="btnConnect-${id}" class="submit-password-btn">Connect</button>\n            </div>\n        `,id++,wifiListElement.appendChild(listItem)}))}function togglePasswordInput(id){$(".password-div").slideUp();const passwordDiv=document.getElementById(`divPass-${id}`);"block"===passwordDiv.style.display?passwordDiv.style.display="none":passwordDiv.style.display="block"}function submitCredentials(id){$(`#btnWiFi-${id}`).text(),$(`#inputPass-${id}`).val()}