function displayWiFiNetworks(){$.getJSON("/networks.json",(function(data){document.getElementById("subtitle").innerText="Available Wi-Fi Networks";const wifiListElement=document.getElementById("wifiList");let id=0;data.networks.forEach((network=>{const listItem=document.createElement("li");listItem.innerHTML=`\n                <div id="wifiitem-${id}" class="wifi-item">\n                    <button id="btnWiFi-${id}" class="show-password-btn"><strong>${network.name}</strong></button>\n                    <span class="signal">${network.rssi}</span>\n                </div>\n                <div id="divPass-${id}" class="password-div">\n                    <input id="inputPass-${id}" class="password-input" type="password" placeholder="Enter WiFi password" />\n                    <button id="btnConnect-${id}" class="submit-password-btn">Connect</button>\n                </div>\n            `,id++,wifiListElement.appendChild(listItem)}))}))}function togglePasswordInput(id){$(".password-div").slideUp();const passwordDiv=document.getElementById(`divPass-${id}`);"block"===passwordDiv.style.display?passwordDiv.style.display="none":passwordDiv.style.display="block"}function submitCredentials(id){const ssid=$(`#btnWiFi-${id}`).text(),pass=$(`#inputPass-${id}`).val();$.ajax({url:"/connect.json",dataType:"json",method:"POST",cache:!1,headers:{ssid:ssid,pass:pass},data:""}),window.alert("Credentials submitted!")}$(document).ready((function(){displayWiFiNetworks()})),$(document).on("click",".wifi-item",(function(){togglePasswordInput($(this).attr("id").split("-")[1])})),$(document).on("click",".submit-password-btn",(function(){submitCredentials($(this).attr("id").split("-")[1])}));